mapper.insert:
INSERT INTO MEMBER(MEMBER_ID
	, MEMBER_PW
	, MEMBER_NAME
	, MEMBER_MAIL)
VALUES(#{memberID}
	, #{memberPW}
	, #{memberName}
	, #{memberMail})

mapper.select:
SELECT COUNT(MEMBER_ID)
FROM member
WHERE MEMBER_ID = #{memberID}

SELECT MEMBER_ID AS memberID
	, MEMBER_PW AS memberPW
	, MEMBER_NAME AS memberName
FROM member
WHERE MEMBER_ID = #{memberID}
AND MEMBER_PW = #{memberPW}

mapper.update:
UPDATE MEMBER
	SET MEMBER_LOGIN_DATE = SYSDATE
WHERE MEMBER_ID = #{memberID}

UPDATE MEMBER
	SET MEMBER_HUMAN = 'Y'
WHERE MEMBER_ID = #{memberID}
