mapper.insert:

SELECT NOTICE_SEQUENCE.NEXTVAL FROM DUAL

INSERT INTO NOTICE(NOTICE_SERIAL_NUMBER
, NOTICE_TITLE
, NOTICE_CONTENT
, NOTICE_WRITER) 
VALUES(#{noticeNumber}
, #{noticeTitle}
, #{noticeContent}
, #{noticeWriter})

mapper.update:
UPDATE notice 
SET NOTICE_TITLE = #{noticeTitle}
, NOTICE_CONTENT = #{noticeContent}
, NOTICE_UPDATE_DATE = now() 
WHERE NOTICE_SERIAL_NUMBER = #{noticeNumber}
